                            dialogs.inputQuery("Carteira", "Novo valor:", "",
                               function (valorPreenchido)
                                          sheet.Soma = (sheet.Soma or 0 ) + valorPreenchido; 
                            end);
                            end;



        -- Comeco do layout

                self.LojaArmaduras.visible = false;
                self.ApresentaLoja.visible = true;


